	if( !function_exists('kingster_gdlr_core_portfolio_single_related') ){
		function kingster_gdlr_core_portfolio_single_related( $args ) {

			$enble_related_portfolio = kingster_get_option('general', 'enable-related-portfolio', 'enable');
			if( $enble_related_portfolio == 'enable' ){
				$args['portfolio-style'] = kingster_get_option('general', 'related-portfolio-style', 'grid');
				$args['num-fetch'] = kingster_get_option('general', 'related-portfolio-num-fetch', 4);
				$args['column-size'] = kingster_get_option('general', 'related-portfolio-column-size', 15);
				$args['thumbnail-size'] = kingster_get_option('general', 'related-portfolio-thumbnail-size', 'medium');
				$args['excerpt-number'] = kingster_get_option('general', 'related-portfolio-num-excerpt', 20);
